import React, { useEffect, useState, useCallback } from 'react'
import { useFunctionBlockStyles } from "../FunctionBlocks/FunctionBlock/style"
import { Variable, DataType, InOutType, SmartComponent, AssociatedSmartComponent } from "../../model/index"
import { Grid, FormControl, InputLabel, Select, Button, MenuItem } from "@material-ui/core"
import { useMountEffect } from '../../utils/main'
import { getOrDownloadSmartComponents } from '../../utils/smartComponents'
import { RequestResponseState } from '../../services/api/api'
import { useStore } from '../templates/Store/Store'
import { SmartComponentActions } from '../../redux/actions'

interface SmartComponentProps {
    setSmartComponents: Function
    smartComponents: AssociatedSmartComponent[]
    title: string
    onSmartComponentRemoval: (smartComponent: SmartComponent) => void
    onSmartComponentEdition: (oldSmartComponent: AssociatedSmartComponent, newSmartComponent: AssociatedSmartComponent) => void
}

export const SmartComponentList = React.memo((props: SmartComponentProps) => {

//   const {smartComponents, setSmartComponents,onSmartComponentRemoval, onSmartComponentEdition} = props

   const classes = useFunctionBlockStyles()

//   const newSmartComponent : AssociatedSmartComponent= {
//     variableId: -1,
//     variableDataType: DataType.dtString,
//     variableOpcua: "",
//     variableInoutType: props.inOutType,
//     variableName: "",
//   }

//   const [newVariable,setNewVariable] : [Variable,Function] = useState(newVar)

//   const setVariable = useCallback((variable:Variable,index:number) => {

//     setSmartComponents((prevVars: Variable[]) => {

//       const oldVariable : Variable = prevVars[index]
//       const newVariables : Variable[] = [...prevVars]
//       newVariables[index] = variable
//       onVariableEdition(oldVariable,variable)
//       return newVariables

//     }) 

//   },[onVariableEdition, setVariables])

//   const addSmartComponent = useCallback((scToAdd: SmartComponent) => {

//     setSmartComponents((prevVariables: Variable[]) =>[...prevVariables, varToAdd])
//     setNewVariable(newVar)

//   }, [setVariables,newVar]) 

//   const removeVariable = useCallback((variableToRemove:Variable) => {
//     setVariables((prevVars: Variable[]) => prevVars.filter((variable:Variable,index:number) => variable !== variableToRemove))
//     onVariableRemoval(variableToRemove)
//   },[onVariableRemoval,setVariables]) 

  const [fetching,setFetching] = useState(true)
  const {data:smartComponents, dispatchAction:dispatchSmartComponentActions} = useStore('smartComponents')
  const updateSmartComponents = (scs: SmartComponent[]) => dispatchSmartComponentActions(SmartComponentActions.updateSmartComponent(scs))
  const [error,setError] = useState('')

   // Recuperar diretamente da comunicação OPC-UA os smart_components disponíveis (SmartComponent)
   useMountEffect(() => {

    setTimeout(() => {

    setFetching(true)
    getOrDownloadSmartComponents(smartComponents)
        .then((result: SmartComponent[]) => updateSmartComponents(result))
        .catch((e:RequestResponseState) => setError(e.msg))
        .finally(() => setFetching(false))
    }, 0)
    })

    // Associar um smartComponent à funcionalidade
    const [smartComponentChoice, setSmartComponentChoice] = useState('')
    const [smartComponentIdChoice, setSmartComponentIdChoice] = useState(0)

    const handleSmartComponentChoice = (event: React.ChangeEvent<{ value: unknown }>) => {
    setSmartComponentChoice(event.target.value as string);
    let i = 0;
    while(i < smartComponents.length)
    {
        if( smartComponents[i].scName === (event.target.value as string))
        {
            setSmartComponentIdChoice(smartComponents[i].scId)
        }
        i++;
    }
    };

  return (
    <Grid container direction="column">
      <Grid item>{props.title}</Grid>
      <Grid item className={classes.box}>
        {smartComponents.map((smartComponent:SmartComponent, index:number) => <SmartComponentRowProps key={index} index={index} addRemoveSmartComponent={removeVariable} setVariable={setVariable} smartComponent={smartComponent} newSmartComponent={false} />)}
        <SmartComponentRowProps index={-1} addRemoveSmartComponent={addVariable} setVariable={setNewVariable} variable={newVariable} newSmartComponent={true}  />
      </Grid> 
    </Grid>
  )
})

// interface VariableRowProps {
//   index: number 
//   addRemoveVariable: (variable: Variable) => void
//   setVariable:Function
//   variable: Variable
//   newVar: boolean
//   order?: number
// }

interface SmartComponentRowProps {
    index: number 
    addRemoveSmartComponent: (smartComponent: SmartComponent) => void
    setSmartComponent:Function
    smartComponent: SmartComponent
    newSmartComponent: boolean
    order?: number
  }

const VariableRow = React.memo((props: VariableRowProps) => {

  const classes = useFunctionBlockStyles()

  const [validName,setValidName] = useState("")

  const action = () => {

    if(!isValid() && props.newVar) 
      setValidName("Mandatory")

    else
      props.addRemoveVariable(props.variable)

  }

  const isValid = useCallback(() => props.variable.variableName !== '', [props.variable])

  const validate = useCallback(() => {

    !isValid() ? setValidName("Mandatory") : setValidName('')

  },[isValid]) 

  useEffect(() => {if(!props.newVar) validate()}, [props.variable.variableName, props.newVar, validate])

  const updateField = (field:string) => (event:any) => {

    setValidName('')
    props.setVariable({...props.variable, [field]: event.target.value},props.index)

  }

  return (
    <Grid item container direction="row" justify="space-between" alignItems="center">
      { props.order ?
      <Grid item xs={1}>
          {props.order}
      </Grid>
      : null
      }
      <Grid item xs={3}>
        <FormControl fullWidth required>
          <InputLabel id={`data-type-label-${props.variable.variableName}`}>SmartComponents</InputLabel>
          <Select
            labelId={`data-type-label-${props.variable.variableName}`}
            value={props.variable.variableDataType}
            onChange={updateField('variableDataType')}
          >
            {Object.values(DataType).map((dt:any, index:number) => <MenuItem key={index} value={dt}>{dt}</MenuItem>)}

          </Select>
        </FormControl>
      </Grid>
      <Grid item xs={1}>
        <Button 
          className={classes.varEvButton} 
          size="small" 
          variant="contained"
          onClick={action}
        >
        {props.newVar ? '+' : 'x' }</Button>
      </Grid>
    </Grid>
  )
}
