import React, { useState, useCallback, useEffect } from 'react'
import { TextField, Grid, Button, InputLabel, Select } from '@material-ui/core'
import { useFunctionBlockStyles } from '../FunctionBlocks/FunctionBlock/style'
import { Done, Clear } from '@material-ui/icons'
import { ConfirmActionProps, ConfirmActionStateLabel, ConfirmActionAction, ConfirmAction } from '../templates/ConfirmAction/ConfirmAction'
import { useMountEffect } from '../../utils/main'
import { AssociatedSmartComponent, SmartComponent } from '../../model'
import { RequestResponseState } from '../../services/api/api'
import { useStore } from '../templates/Store/Store'
import { AssociatedSmartComponentActions, SmartComponentActions } from '../../redux/actions'
import { getOrDownloadAssociatedSmartComponents } from '../../utils/associatedSmartComponents'

interface DigitalTwinProps {

    dtName: {
        dtName: string
        setDtName: Function
    },
    cancel: {
        label: string
        action: () => void
    },
    confirmDialog: {
        buttonTitle: string
        title: string
        initialState: ConfirmActionStateLabel
        action: ConfirmActionAction
        onSuccess : () => void
        start? : {
            label: string
            positiveLabel: string
            negativeLabel: string
        }
        success: {
            label:string
            positiveLabel: string
        }
        error: {
            label: string
            positiveLabel: string
        }
    },
}

interface AssociatedSmartComponentsProps {

  scName: {
      dtName: string
      setDtName: Function
  },
}

export const DigitalTwinForm = (propsDt: DigitalTwinProps, propsSc: AssociatedSmartComponentsProps) => {

    /**styles */
    const classes = useFunctionBlockStyles()

    /**Digital Twin Name*/
    const {dtName, setDtName} = propsDt.dtName
    const [validDtName, setValidDtName] = useState(true)
    const onChangeDtName = useCallback((event:any) => {

        setDtName(event.target.value.trim())
        setValidDtName(true)

      },[setDtName])

    /* Buttons */

    const onCancel = propsDt.cancel.action
    const cancelLabel = propsDt.cancel.label

    const confirmLabel = propsDt.confirmDialog.buttonTitle

    /* Confirmation */

    const [confirm, setConfirm] : [boolean, Function] = useState(false)

    const validateFields = () : boolean => {

        const cValidDtName = dtName.trim() !== ''

        if(!cValidDtName) setValidDtName(cValidDtName)

        return cValidDtName

    }

    const confirmButtonAction = () => {

        if(validateFields())
          setConfirm(true)
    }

    const confirmActionStates = {
        start: {
            label: propsDt.confirmDialog.start?.label,
            positiveLabel: propsDt.confirmDialog.start?.positiveLabel,
            negativeLabel: propsDt.confirmDialog.start?.negativeLabel,
            state: ConfirmActionStateLabel.start
        },
        executing: {
          label: '',
          state: ConfirmActionStateLabel.executing,
        },
        success: {
          label: propsDt.confirmDialog.success.label,
          state: ConfirmActionStateLabel.success,
          positiveLabel: propsDt.confirmDialog.success.positiveLabel
        },
        error: {
          label: propsDt.confirmDialog.error.label,
          state: ConfirmActionStateLabel.error,
          positiveLabel: propsDt.confirmDialog.success.positiveLabel
        },
      }

    const confirmActionProps : ConfirmActionProps = {

        title: propsDt.confirmDialog.title,
        states: confirmActionStates,
        currentState: confirmActionStates.executing,
        onCancel: () => setConfirm(false),
        onError: () => setConfirm(false),
        onSuccess: propsDt.confirmDialog.onSuccess,
        action:propsDt.confirmDialog.action,

    }

    //MANIPULAR OS SMART COMPONENTS DISPONÍVEIS

    const [fetching,setFetching] = useState(true)
    const {data:smartComponents, dispatchAction:dispatchSmartComponentActions} = useStore('smartComponents')
    const updateSmartComponents = (sc: SmartComponent[]) => dispatchSmartComponentActions(SmartComponentActions.updateAssociatedSmartComponents(sc))
    const [error,setError] = useState('')

     // Recuperar diretamente da comunicação OPC-UA os smart_components disponíveis (SmartComponent)
     useMountEffect(() => {

      setTimeout(() => {

      setFetching(true)
      getOrDownloadSmartComponents(smartComponents)
          .then((result: AssociatedSmartComponent[]) => updateAssociatedSmartComponents(result))
          .catch((e:RequestResponseState) => setError(e.msg))
          .finally(() => setFetching(false))
      }, 0)
      })

    console.log(associatedSmartComponents)

    // Associar um smartComponent ao Digital Twin
    const [associatedSmartComponentChoice, setAssocieatedSmartComponentChoice] = useState('')
    const [associatedSmartComponentIdChoice, setAssociatedSmartComponentIdChoice] = useState(0)

    // const handleSmartComponentChoice = (event: React.ChangeEvent<{ value: unknown }>) => {
    // setSmartComponentChoice(event.target.value as string);
    // let i = 0;
    // while(i < smartComponents.length)
    // {
    //     if( smartComponents[i].scName === (event.target.value as string))
    //     {
    //         setSmartComponentIdChoice(smartComponents[i].scId)
    //     }
    //     i++;
    // }


    return (
        <> {confirm ? <ConfirmAction {...confirmActionProps} /> : null}
            <Grid spacing={1} justify="space-between" item xs container direction="row">
              <Grid item xs={6}>
                <TextField
                  onChange={onChangeDtName} 
                  value={dtName}
                  helperText=""
                  error={!validDtName} 
                  fullWidth required 
                  label="Insert New Digital Twin Name" 
                  id="dt-name" 
                  type="text" />
            </Grid>
            {/* <Grid item xs={12} sm={6}>
              <InputLabel id={`digital-twin-label-${associatedSmartComponents}`}>DINASORE</InputLabel>
                <Select labelId={`digital-twin-label-${associatedSmartComponents}`} value={smartComponentChoice} onChange={handleSmartComponentChoice}>
                    {(associatedSmartComponents || []).map((smartComponent: any) => {return <MenuItem key={smartComponent.scId} value={smartComponent.scName}>{smartComponent.scName}</MenuItem>})}
                </Select> 
            </Grid> */}
            </Grid>
            <Grid item xs container direction="row" justify="space-between" alignItems="center"> 
              <Grid item xs={6} container direction="row" spacing={2} alignItems="center" justify="flex-end">
                <Grid item>
                    <Button onClick={onCancel} color="secondary" variant="contained"><Clear/>{cancelLabel}</Button>
                </Grid>
                <Grid>
                  <Button onClick={confirmButtonAction} color="primary" variant="contained"><Done/>{confirmLabel}</Button>
                </Grid>
              </Grid>
            </Grid>
        </>
    )
}
